import { createStore } from 'vuex'

export default createStore({
  state: {
    user: null,
    todolistList: []
  },
  getters: {
    user: (state) => {
      return state.user
    },
    todolistList: (state) => {
      return state.todolistList
    }
  },
  actions: {
    user (context, user) {
      context.commit('user', user)
    },
    fetchUserData (context, todolistList) {
      context.commit('todolistList', todolistList)
    }
  },
  mutations: {
    user (state, user) {
      state.user = user
    },
    todolistList (state, todolistList) {
      state.todolistList = todolistList
    }
  }
})
