import { createStore } from 'vuex'

export default createStore({
  state: {
    user: null,
    todolists: [],
    tasks: []
  },
  getters: {
    user: (state) => {
      return state.user
    },
    todolists: (state) => {
      return state.todolists
    },
    tasks: (state) => {
      return state.tasks
    }
  },
  actions: {
    user (context, user) {
      context.commit('user', user)
    },
    todolists (context, todolists) {
      context.commit('todolists', todolists)
    },
    tasks (context, data) {
      console.log(data.tasks)
      console.log(data.idTodolist)
      context.commit('tasks', { data })
    }
  },
  mutations: {
    user (state, user) {
      state.user = user
    },
    todolists (state, todolists) {
      state.todolists = todolists
    },
    tasks (state, data) {
      state.tasks[data.idTodolist] = data.tasks
    }
  }
})
