import { createStore } from 'vuex'
import axios from 'axios'

export default createStore({
  state: {
    user: null,
    todolists: []
  },
  getters: {
    user: (state) => {
      return state.user
    },
    todolists: (state) => {
      return state.todolists
    }
  },
  actions: {
    user (context, user) {
      context.commit('user', user)
    },
    async fetchTodolists ({ commit }, userId) {
      const response = await axios.get(`/api/todolists/${userId}`)
      commit('setTodolists', response.data)
    }
  },
  mutations: {
    user (state, user) {
      state.user = user
    },
    setTodolists (state, todolists) {
      state.todolists = todolists
    }
  }
})
